mapper.select:
select a.song_id,
a.song,
a.singer_id,
a.play_count,
a.download_count,
a.url,
a.language,
a.album_id,
a.time,
b.singer_id,
b.singer_name,
b.sex,
b.img_url,
b.birthday,
b.introduction,
c.album
from song a,singer b,album c

    c.singer_id = b.singer_id
    and b.singer_id = a.singer_id

        and a.song_id = #{song.songId,jdbcType=INTEGER}


